import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AddUserInfo() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [target, setTarget] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        const response = await fetch('/add_user_info', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ tunnus: username, salasana: password, kohde: target }),
            credentials: 'include' 
        });
        const data = await response.json();
        if (data.success) {
            navigate('/dashboard');
        } else {
            alert(data.message);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Tunnus" required />
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Salasana" required />
            <input type="text" value={target} onChange={(e) => setTarget(e.target.value)} placeholder="Kohde" required />
            <button type="submit">Lisää tiedot</button>
        </form>
    );
}

export default AddUserInfo;
