import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (e) => {
        e.preventDefault();

        const credentials = {
            kayttaja: username,
            salasana: password
        };

        fetch('http://localhost:5555/login', {  // Olettaen, että login endpoint on /login
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(credentials),
        })
            .then(response => response.json())
            .then(data => {
                if (data.success) {
                    setMessage('Kirjautuminen onnistui!');
                    // Tee jotain kirjautumisen jälkeen, kuten navigointi pääsivulle
                    navigate('/dashboard'); // Esimerkki, voi muuttaa tarpeen mukaan
                } else {
                    setMessage('Kirjautuminen epäonnistui. Tarkista käyttäjänimi ja salasana.');
                }
            })
            .catch(error => console.error('Error:', error));
    };

    return (
        <div>
            <h1>Kirjaudu sisään</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Käyttäjänimi:
                    <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
                </label>
                <br />
                <label>
                    Salasana:
                    <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
                </label>
                <br />
                <button type="submit">Kirjaudu</button>
            </form>
            <p>{message}</p>
            <button onClick={() => navigate('/adduser')}>Lisää uusi käyttäjä</button>
        </div>
    );
};

export default Login;
